mapper.select:
		select *
		from basketTB 
        where username = #{param1}
        	and ticket_id = #{param2}
        	and validity = 1

		select cnt 
		from basketTB 
        where username = #{param1}
        	and ticket_id = #{param2}
        	and validity = 1


		select seat_able 
		from ticketTB 
        where ticket_id = #{param1}

mapper.update:
		update basketTB
		set cnt = #{param3}
		where username = #{param1}
        	and ticket_id = #{param2}

		update basketTB
		set validity = 0
		where username = #{param1}
        	and ticket_id = #{param2}

mapper.delete:
delete from basketTB 
where username = #{param1}
	and ticket_id = #{param2}

mapper.update:
update basketTB
set username = #{param2}
where username = #{param1}
